config.files:
{
    "server.properties": {
        "parser": "properties",
        "find": {
            "server-ip": "0.0.0.0",
            "server-port": "{{server.build.default.port}}"
        }
    },
    "./autoplug/general.yml": {
        "parser": "file",
        "find": {
            "    key:": "    key: {{server.build.env.SERVER_KEY}}",
            "    start-command: ": "    start-command: java -Xms128M -Xmx{{server.build.memory}}M {{server.build.env.JAVA_ARGS}} -jar \"./{{server.build.env.SERVER_TYPE}}.jar\""
        }
    },
    "./autoplug/updater.yml": {
        "parser": "file",
        "find": {
            "    software:": "    software: {{server.build.env.SERVER_TYPE}}",
            "    version: PLUGINS_VERSION": "    version: {{server.build.env.GAME_VERSION}}",
            "    version: MODS_VERSION": "    version: {{server.build.env.GAME_VERSION}}",
            "    enable: true # Java": "    enable: {{server.build.env.UPDATE_JAVA}} # Java",
            "    enable: true # Server": "    enable: {{server.build.env.UPDATE_SERVER}} # Server",
            "    enable: true # Plugins": "    enable: {{server.build.env.UPDATE_PLUGINS}} # Plugins",
            "    enable: true # Mods": "    enable: {{server.build.env.UPDATE_MODS}} # Mods",
            "    enable: true # AutoPlug": "    enable: {{server.build.env.UPDATE_AUTOPLUG}} # AutoPlug"
        }
    }
}
